import { useState, useEffect } from 'react'
import axios from 'axios'
import { BASE_URL } from '../globals'



const Excuse = ({displayExcuse}) => {
  const [excuse, setExcuse] = useState('')


  useEffect(() => {
    const getExcuse = async () => {
      try{

      const response = await axios.get(`${BASE_URL}`)
      console.log(response.data)
      switch (response.data.Tag) {

        case "inspiration":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "design":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "animation":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "typography":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "color":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "tools":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "development":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "presentation":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "presentation":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "email":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "progress":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "starting":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

        case "disagreement":
        setExcuse(`${BASE_URL}/${response.data.Tag}`.Excuse);
        break;

      }

      }catch (err){
          console.log(err);
      }
  }
  getExcuse()
  }, [displayExcuse])

  return (

    <div key={excuse.id} className="card">
    <h2 style={{ fontStyle: 'italic' }}>{excuse!=='' && excuse}</h2>
    </div>

  )
}

export default Excuse
