import './styles/App.css'
import { useState, useEffect } from 'react'
import {BASE_URL, API_KEY} from './globals'
import axios from 'axios'
import MovieList from './components/MovieList'
import MovieDetails from './components/MovieDetails'

const App = () => {
  const [movies, setMovies] = useState([])
  const [moviesDetails, setMoviesDetails] = useState([])
  const [selectedMovie, setSelectedMovie] = useState(null) // this should be a movie once you select one


  useEffect(()=>{

    const getMovies = async () => {

      const res = await axios.get(`${BASE_URL}/discover/movie?api_key=${API_KEY}`)

      setMovies(res.data.results)
      console.log(res)
    }

    getMovies()


    function getMovieDetails() {
      document.getElementById("demo").innerHTML = "YOU CLICKED ME!";
    }

  }, [])

  return (
    <div>
      {/* if selected movie is not null show selected movie, otherwise show movie list
      Conditional rendering  */}
      <MovieList movies = {movies} />
      <MovieDetails moviesDetails ={movies} />

    </div>
  )
}

export default App
