import './styles/App.css'
import { useState, useEffect } from 'react'
import axios from 'axios'
import { BASE_URL, API_KEY } from './globals'
import Excuse from './components/Excuse'


const App = () => {
  const [displayExcuse, setExcuse] = useState(false)

  const toggleExcuses = () => {
    {displayExcuse ? (
      setExcuse(false)
      ) : (
        setExcuse(true)
      )
    }
  }

  return (
    <div className="App">
      <main>
      <div class="instructions">
        <header><h1>Excuses Generator</h1></header>
        <h3>Need an excuse? Click on the button for an idea on how to further delay your responsibilities.</h3>
        <div class="theButton">
        { <button onClick={toggleExcuses}>{displayExcuse===false ? "Escape Accountability!" : "Escape again!"}</button> }
        </div>
        </div>
        <div className="quote-container">

          {displayExcuse===false ? 
          <h2>Escape your problems</h2>
          :<Excuse displayExcuse={displayExcuse} />}

        </div>

      </main>
    </div>
  )
}

export default App
