import './styles/App.css'
import { useState, useEffect } from 'react'
import axios from 'axios'
import { BASE_URL, API_KEY } from './globals'
import Activity from './components/Activity'


const App = () => {
  const [displayActivity, setActivity] = useState(false)

  const toggleActivities = () => {
    {displayActivity ? (
      setActivity(false)
      ) : (
        setActivity(true)
      )
    }
  }

  return (
    <div className="App">
      <header className="image-container"></header>
      <main>
        <div className="quote-container">
          <h1>Activity Ideas</h1>
          {displayActivity===false ? 
          <h2>Need some inspiration? Click on the button for an idea.</h2>
          :<Activity displayActivity={displayActivity} />}

        </div>

        <button onClick={toggleActivities}>{displayActivity===false ? "New Idea" : "Clear Idea"}</button>
      </main>
    </div>
  )
}

export default App
